function searchRange(nums, target) {
    const result = [-1, -1];

    let left = 0, right = nums.length - 1;

    // Find the leftmost index
    while (left <= right) {
        const mid = Math.floor((left + right) / 2);
        if (nums[mid] < target) {
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }

    if (left >= nums.length || nums[left] !== target) return result;
    result[0] = left;

    // Reset right pointer for finding the rightmost index
    right = nums.length - 1;

    // Find the rightmost index
    while (left <= right) {
        const mid = Math.floor((left + right) / 2);
        if (nums[mid] <= target) {
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }

    result[1] = right;
    return result;
}

