            while 0 < nums[i] <= n and nums[nums[i] - 1] != nums[i]:
                temp = nums[i]
                nums[i], nums[temp - 1] = nums[temp - 1], temp

        for i in range(n):
            if nums[i] != i + 1:
                return i + 1

        return n + 1

